mapper.select:
select loginUsr.time,CAST(loginUsr.sum/usr.sum as DECIMAL(4,2)) as percent from (select count(distinct userid) as sum, DATE_FORMAT(createtime,"%Y-%m-%d %H:%i:%s") as time from sys_login_log where createtime between DATE_SUB(NOW(),INTERVAL #{dayNum} DAY ) and NOW() group by
DATE_FORMAT(createtime,"%Y-%m-%d %H-00-00")
DATE_FORMAT(createtime,"%Y-%m-%d 00-00-00")
) as loginUsr, (select count(*) as sum from sys_user) as usr;

select loginCmp.time,CAST(loginCmp.sum/cmp.sum as DECIMAL(4,2)) as percent from (select count(distinct slu.uniqueCode) as sum, DATE_FORMAT(slu.createtime,"%Y-%m-%d %H:%i:%s") as time from (select su.unique_code as uniqueCode,sl.createtime from sys_login_log as sl left join sys_user as su on su.id = sl.userid) slu where createtime between DATE_SUB(NOW(),INTERVAL #{dayNum} DAY ) and NOW() group by
DATE_FORMAT(createtime,"%Y-%m-%d %H-00-00")
DATE_FORMAT(createtime,"%Y-%m-%d 00-00-00")
) as loginCmp, (select count(*) as sum from sys_user where id=unique_code) as cmp;

select cmp.cmpNum,usr.userNum from
(select count(*) as cmpNum from sys_user where id=unique_code and create_time < DATE_SUB(NOW(),INTERVAL #{dayNum} DAY) ) as cmp,
(select count(*) as userNum from sys_user where create_time < DATE_SUB(NOW(),INTERVAL #{dayNum} DAY) ) as usr;
